CREATE TABLE DEX_Color
(
	Color_ID int PRIMARY KEY GENERATED ALWAYS AS IDENTITY(START WITH 1, INCREMENT BY 1),
	Color_Name varchar(10)
);


CREATE TABLE DEX_Data
(
	Entry_ID int GENERATED ALWAYS AS IDENTITY(START WITH 1, INCREMENT BY 1),
	Category varchar(20) NOT NULL,
	Color int NOT NULL,
	Entry varchar(500) NOT NULL
);

